import React, { useState, useEffect, history, Link } from 'react';
import Header from '../components/Header';
import { useNavigate } from 'react-router-dom';

//Services
import api from '../services/api';

//Static
import "../static/css/lista.css";

function ListaProdutos() {


    const accessToken = localStorage.getItem('accessToken');

    const [products, setProducts] = useState([]);

    let navigate = useNavigate();

    async function editProduct(id) {
        try {
            navigate(`/cadastroProduto/${id}`)
        } catch (error) {
            alert('Edit failed! Try again.');
        }
    }

    async function deleteProduct(id) {
        var check = window.confirm("Deseja excluir o item selecionado?");

        if (check === false) return

        try {
            await api.delete(`api/v1/items/${id}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })

            setProducts(products.filter(product => product.id !== id))
        } catch (err) {
            alert('Delete failed! Try again.');
        }
    }

    async function fetchMoreProducts() {
        const response = await api.get('api/v1/items', {
            headers: {
                Authorization: `Bearer ${accessToken}`
            }

        });
        setProducts(response.data.data)
    }

    useEffect(() => {
        fetchMoreProducts();
    }, [])

    return (
        <div className="book-container">
            <Header />

            <h1 className='titulo-lista'>Produtos</h1>

            <table className='box-lista'>
                <thead>
                    <tr scope="row">
                        <th className='titulo-campo-lista'>ID:</th>
                        <th className='titulo-campo-lista'>NOME:</th>
                        <th className='titulo-campo-lista'>DESCIRCAO:</th>
                        <th className='titulo-campo-lista'>QUANTIDADE:</th>
                    </tr>
                </thead>
<br/>
                <tbody id="dados">
                    {products.map(product =>
                        <tr key={product.id}>
                            <td className='valor-campo-lista'>{product.id}</td>

                            <td className='valor-campo-lista'>{product.nome}</td>

                            <td className='descricao-campo-lista'>{product.descricao}</td>

                            <td className='valor-campo-lista'>{product.quantidade}</td>

                        </tr>
                    )}
                </tbody>
            </table>



        </div>
    );

}

export default ListaProdutos;
