import React, { useState, useEffect, history, Link } from 'react';
import Header from '../components/Header';
import { useNavigate } from 'react-router-dom';

//Services
import api from '../services/api';

//Static
import "../static/css/lista.css";

function ListaFornecedores() {


    const accessToken = localStorage.getItem('accessToken');

    const [fornecedores, setFornecedores] = useState([]);

    let navigate = useNavigate();

    async function editProvider(id) {
        try {
            navigate(`/cadastroFornecedor/${id}`)
        } catch (error) {
            alert('Edit failed! Try again.');
        }
    }

    async function deleteProvider(id) {
        var check = window.confirm("Deseja excluir o item selecionado?");

        if (check === false) return

        try {
            await api.delete(`api/v1/providers/${id}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })

            setFornecedores(fornecedores.filter(fornecedor => fornecedor.id !== id))
        } catch (err) {
            alert('Delete failed! Try again.');
        }
    }

    async function fetchMoreProviders() {
        const response = await api.get('api/v1/providers', {
            headers: {
                Authorization: `Bearer ${accessToken}`
            }

        });

        console.log([response.data])
        setFornecedores(response.data.data)
    }

    useEffect(() => {
        fetchMoreProviders();
    }, [])

    return (
        <div className="book-container">
            <Header />

            <h1 className='titulo-lista'>Fornecedor</h1>
            <table class="box-relatorio">
                <thead>
                    <tr scope="row">
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Emporesa</th>
                        <th>Contato</th>
                        <th>Email</th>
                    </tr>
                </thead>

                <tbody id="dados">
                    {fornecedores.map(fornecedor =>
                        <tr key={fornecedor.id}>
                            <td>{fornecedor.nome}</td>
                            <td>{fornecedor.empresa}</td>
                            <td>{fornecedor.contato}</td>
                            <td>{fornecedor.email}</td>
                        </tr>
                    )}

                </tbody>


            </table>


        </div >


    );

}

export default ListaFornecedores;
