import React, { useState, useEffect, history, Link } from 'react';
import Header from '../components/Header';
import { useNavigate } from 'react-router-dom';

//Services
import api from '../services/api';

//Static
import "../static/css/lista.css";

function ListaFornecedores() {


    const accessToken = localStorage.getItem('accessToken');

    const [fornecedores, setFornecedores] = useState([]);

    let navigate = useNavigate();

    async function editProduct(id) {
        try {
            navigate(`/cadastroFornecedor/${id}`)
        } catch (error) {
            alert('Edit failed! Try again.');
        }
    }

    async function deleteProduct(id) {
        var check = window.confirm("Deseja excluir o item selecionado?");

        if (check === false) return;

        try {
            await api.delete(`api/v1/provider/${id}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })

            setFornecedores(fornecedores.filter(fornecedor => fornecedor.id !== id))
        } catch (err) {
            alert('Delete failed! Try again.');
        }
    }

    async function fetchMoreProducts() {
        const response = await api.get('api/v1/providers', {
            headers: {
                Authorization: `Bearer ${accessToken}`
            }

        });

        console.log([response.data])
        setFornecedores(response.data.data)
    }

    useEffect(() => {
        fetchMoreProducts();
    }, [])

    return (
        <div className="book-container">
            <Header/>

            <h1 className='titulo-lista'>Fornecedores</h1>
            <table className='box-lista'>
                {fornecedores.map(fornecedor => (
                    <tbody className='lista' key={fornecedor.id}>

                        <td className='titulo-campo-lista'>ID:</td>
                        <td className='valor-campo-lista'>{fornecedor.id}</td>
                        <td className='titulo-campo-lista'>NOME:</td>
                        <td className='valor-campo-lista'>{fornecedor.nome}</td>
                        <td className='titulo-campo-lista'>EMPRESA:</td>
                        <td className='valor-campo-lista'>{fornecedor.empresa}</td>
                        <td className='titulo-campo-lista'>TELEFONE:</td>
                        <td className='valor-campo-lista'>{fornecedor.telefone}</td>
                        <td className='titulo-campo-lista'>E-MAIL:</td>
                        <td className='valor-campo-lista'>{fornecedor.email}</td>

                        <input className='btn-lista-edit' onClick={() => editProduct(fornecedor.id)}
                         type="submit" value=""/>


                        <input className='btn-lista-delete' onClick={() => deleteProduct(fornecedor.id)}
                         type="button" value=""/>

                    </tbody>
                ))}
            </table>
        </div>
    );

}

export default ListaFornecedores;
